import React, { useState } from 'react'
import * as Icon from 'react-feather'
import { BrowserRouter, Route, Routes, HashRouter, useLocation } from 'react-router-dom'
import './App.scss'
import ToggleSwitch from './components/toggleSwitch';
import AnimationRoutes from './components/AnimationRoutes'

function App() {
  const location = useLocation()
  const [lightMode, setLightMode] = useState(false)
  lightMode ? document.body.classList.add('light') : document.body.classList.remove('light')

  return (

    <HashRouter>
          
      
    <div className="light-mode">
        <span className="icon">
          <Icon.Sun />
        </span>
        <button
          className={
            lightMode ? "light-mode-switch active" : "light-mode-switch"
          }
          onClick={() => handleMode()}
        ></button>
      </div>
     
      <AnimationRoutes />
    </HashRouter>
  )
}

export default App
