import React, { useState } from 'react'
import * as Icon from 'react-feather'
import { BrowserRouter, Route, Routes, HashRouter, useLocation } from 'react-router-dom'
import './App.scss'
import AnimationRoutes from './components/AnimationRoutes'

function App() {
  // Made it true if you want to load your site light mode primary
  console.log(process.env.PUBLIC_URL)

  return (
    <HashRouter>
      
            <div className="light-mode">
        <span className="icon">
          <Icon.Sun />
        </span>
        <button
          className={
            lightMode ? "light-mode-switch active" : "light-mode-switch"
          }
          onClick={() => handleMode()}
        ></button>
      </div>
     
      <AnimationRoutes />
    </HashRouter>
  )
}

export default App
