import axios from "axios";
import React, { Suspense, useEffect, useState } from "react";
import { Helmet } from "react-helmet";
import Layout from "../components/Layout";
import Progress from "../components/Progress";
import Resume from "../components/Resume";
import Sectiontitle from "../components/Sectiontitle";
import Smalltitle from "../components/Smalltitle";
import Spinner from "../components/Spinner";
import { GlobalStyle } from "./globleStyle";
import { motion } from "framer-motion";
import { Chrono } from "react-chrono";


function Resumes() {
  const [skills, setSkills] = useState([]);
  const [workingExperience, setWorkingExperience] = useState([]);
  const [educationExperience, setEducationExperience] = useState([]);
  console.log("workingExperience123", workingExperience)
useEffect(() => {
    axios.get("/api/skills").then((response) => {
      setSkills(response.data);
    });
    axios.get("/api/experience").then((response) => {
      setWorkingExperience(response.data.workingExperience);
      
      setEducationExperience(response.data.educationExperience);
    });
  }, []);

    const items = [
      {
        title: '2018 - Present',
        cardTitle: 'Frontend Web Developer',
        cardDetailedText: ["UI/UX Designer", "UI/UX Designer"],
        },
        
      {
        title: '2016 - 2018',
        cardTitle: 'Frontend Web Developer',
        cardDetailedText: ["UI/UX Designer", "UI/UX Designer"],
        },
      {
        title: '2014 - 1016',
        cardTitle: 'UI/UX Designer',
        cardDetailedText: ["UI/UX Designer", "UI/UX Designer"],
        },
    ]

    
 
 
  return (
    <Layout>
      <GlobalStyle />
      <Helmet>
        <title>Resume - Chester React Personal Portfolio Template</title>
        <meta
          name="description"
          content="Chester React Personal Portfolio Template Resume Page"
        />
      </Helmet>
      <motion.div className="container"        
      intial={{width:0}}
         animate={{width:'100%'}}
         exit={{x:window.innerWidth, transition:{duration:1}}}>
      <Suspense fallback={<Spinner /> }>

        <div className="mi-resume-area mi-section mi-padding-top mi-padding-bottom">
          <div className="container">
            <Sectiontitle title="Resume" />
            <Smalltitle title="Working Experience" icon="briefcase" />
            <div className="mi-resume-wrapper">




            <motion.div style={{ width: 'auto', height: '950px' }} className="mi-service" >

            <Chrono items={items} mode="VERTICAL"    
fontSizes={{
  cardSubtitle: '0.85rem',
  cardText: '0.8rem',
  cardTitle: '1rem',
  title: '1rem',
}}
  theme={{


   
    primary: '#ff0057',
    secondary: '#ff0057',
    cardSubtitleColor: "white",
    titleColor: 'white',
    titleColorActive: 'white',

// cardForeColor: "#006A5C",
// cardDetailedText: "#006A5C",
  }}
  />
            </motion.div>
            </div>
          </div>
        </div>
      </Suspense>
      </motion.div>
    </Layout>
  );
}

export default Resumes;
